import React, { Component } from 'react';
import Layout from '../../components/Layout';
import { Button, Form, Input, Message } from 'semantic-ui-react';
import factory from '../../ethereum/factory';
import web3 from '../../ethereum/web3';

class CampaignNew extends Component {
  state = {
    minimunContribution: '',
    errorMessage: '',
    loading: false
  };

  onSubmit = async (event) => {
    event.preventDefault();
    this.setState({ loading: true });
    try{
      const accounts = await web3.eth.getAccounts();
      await factory.methods.createCampaign(this.state.minimunContribution).send({
        from: accounts[0]
      });
    }catch(err){
      this.setState({errorMessage: err.message });
    }

  };

  render() {
    return (
      <Layout>
      <h3>Create a Campaign</h3>

      <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
        <Form.Field>
           <label>Minimum Contribution</label>
           <Input
            label='wei'
            labelPosition='right'
            value={this.state.minimunContribution}
            onChange={ event => this.setState({minimunContribution: event.target.value })}
            />
         </Form.Field>
         <Message
          error
          header='Opps'
          content={this.state.errorMessage}
          />
         <Button loading primary>Create!</Button>
      </Form>
      </Layout>
    );
  }
}


export default CampaignNew;
